SELECT i.i_brand, i.i_class, i.i_category, d.d_year, SUM(cs.cs_sales_price) AS total_sales FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_category = 'Books' AND d.d_year IN (2002, 2003) GROUP BY i.i_brand, i.i_class, i.i_category, d.d_year ORDER BY i.i_brand, i.i_class, i.i_category, d.d_year;
