SELECT AVG(ws_quantity) AS avg_quantity, AVG(ws_list_price) AS avg_list_price, AVG(ws_ext_discount_amt / NULLIF(ws_quantity, 0)) AS avg_discount, AVG(ws_sales_price) AS avg_sales_price FROM web_sales JOIN promotion ON ws_promo_sk = p_promo_sk JOIN customer_demographics ON ws_bill_cdemo_sk = cd_demo_sk WHERE p_channel_dmail = 'N' AND p_channel_event = 'N' AND ws_warehouse_sk IS NOT NULL AND ws_ship_hdemo_sk IS NOT NULL AND ws_bill_hdemo_sk IS NOT NULL AND cd_gender = 'F' AND cd_marital_status = 'M' AND cd_education_status = 'Advanced Degree';
