SELECT c.c_customer_id, SUM(ss.ss_coupon_amt) AS total_coupon_amount, SUM(ss.ss_net_profit) AS total_net_profit FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN household_demographics hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d.d_day_name = 'Monday' AND d.d_year BETWEEN 2020 AND 2022 AND hd.hd_dep_count > 2 AND hd.hd_vehicle_count > 2 AND s.s_floor_space > 50000 GROUP BY c.c_customer_id;
