SELECT AVG(ss.ss_quantity) AS avg_quantity, AVG(ss.ss_list_price) AS avg_list_price, AVG(ss.ss_ext_discount_amt) AS avg_discount, AVG(ss.ss_sales_price) AS avg_sales_price FROM store_sales ss JOIN promotion p ON ss.ss_promo_sk = p.p_promo_sk JOIN customer_demographics cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE p.p_channel_dmail = 'N' AND p.p_channel_event = 'N' AND d.d_year = 2000 AND cd.cd_gender = 'M' AND cd.cd_marital_status = 'S' AND cd.cd_education_status = 'College';
