SELECT AVG(ss_quantity) AS avg_quantity, AVG(ss_list_price) AS avg_list_price, AVG(ss_list_price - ss_sales_price) AS avg_discount, AVG(ss_sales_price) AS avg_sales_price FROM store_sales JOIN promotion ON ss_promo_sk = p_promo_sk JOIN customer_demographics ON ss_cdemo_sk = cd_demo_sk JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE p_channel_dmail = 'N' AND p_channel_event = 'N' AND d_year = 1998 AND cd_education_status = 'College' AND cd_marital_status = 'S' AND cd_gender = 'F';
