SELECT AVG(ss.ss_quantity) AS avg_quantity, AVG(ss.ss_list_price) AS avg_list_price, AVG(ss.ss_list_price - ss.ss_sales_price) AS avg_discount, AVG(ss.ss_sales_price) AS avg_sales_price FROM store_sales ss JOIN promotion p ON ss.ss_promo_sk = p.p_promo_sk JOIN customer_demographics cd ON ss.ss_cdemo_sk = cd.cd_demo_sk WHERE p.p_channel_dmail = 'N' AND p.p_channel_event = 'N' AND cd.cd_gender = 'F' AND cd.cd_marital_status = 'Single' AND cd.cd_education_status = 'College' GROUP BY cd.cd_gender, cd.cd_marital_status, cd.cd_education_status;
