SELECT c.c_customer_id FROM customer c JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN item i ON ws.ws_item_sk = i.i_item_sk AND ss.ss_item_sk = i.i_item_sk WHERE i.i_manufact_id IN (57, 293, 427, 320) GROUP BY c.c_customer_id HAVING SUM(ws.ws_net_paid) > SUM(ss.ss_net_paid);
