SELECT i.i_item_id, SUM(sr.sr_return_quantity) AS store_returns, SUM(cr.cr_return_quantity) AS catalog_returns, SUM(wr.wr_return_quantity) AS web_returns FROM item i JOIN store_returns sr ON i.i_item_sk = sr.sr_item_sk JOIN catalog_returns cr ON i.i_item_sk = cr.cr_item_sk JOIN web_returns wr ON i.i_item_sk = wr.wr_item_sk JOIN date_dim dd ON sr.sr_returned_date_sk = dd.d_date_sk WHERE dd.d_date BETWEEN DATE '2023-10-01' AND DATE '2023-10-07' GROUP BY i.i_item_id HAVING ABS(SUM(sr.sr_return_quantity) - SUM(cr.cr_return_quantity)) <= 0.1 * SUM(sr.sr_return_quantity) AND ABS(SUM(sr.sr_return_quantity) - SUM(wr.wr_return_quantity)) <= 0.1 * SUM(sr.sr_return_quantity) AND ABS(SUM(cr.cr_return_quantity) - SUM(wr.wr_return_quantity)) <= 0.1 * SUM(cr.cr_return_quantity) ORDER BY (SUM(sr.sr_return_quantity) + SUM(cr.cr_return_quantity) + SUM(wr.wr_return_quantity)) DESC LIMIT 1;
