SELECT c.c_customer_id, c.c_first_name, c.c_last_name FROM customer c JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN income_band ib ON hd.hd_income_band_sk = ib.ib_income_band_sk WHERE ca.ca_city = 'Edgewood' AND ib.ib_lower_bound >= 38128 AND ib.ib_upper_bound <= 50000; -- Assuming 50000 as the upper bound for the income range
