SELECT cd.cd_marital_status, cd.cd_education_status, ca.ca_state, ws.ws_net_profit, AVG(ws.ws_sales_price) AS avg_sales, AVG(wr.wr_refunded_cash) AS avg_refunded_cash, AVG(wr.wr_fee) AS avg_return_fee FROM web_returns wr JOIN web_sales ws ON wr.wr_order_number = ws.ws_order_number JOIN customer c ON wr.wr_refunded_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim d ON ws.ws_sold_date_sk = d.d_date_sk WHERE d.d_year = 2001 AND ca.ca_state IN ('KY', 'ME', 'IL', 'OK', 'NE', 'MN', 'FL', 'WI', 'KS') AND cd.cd_education_status IN ('Advanced Degree', 'Primary', 'Secondary') AND cd.cd_marital_status IN ('M', 'S', 'W') GROUP BY cd.cd_marital_status, cd.cd_education_status, ca.ca_state, ws.ws_net_profit;
