SELECT COUNT(DISTINCT cs.cs_bill_customer_sk) AS customer_count FROM catalog_sales cs JOIN web_sales ws ON cs.cs_sold_date_sk = ws.ws_sold_date_sk AND cs.cs_bill_customer_sk = ws.ws_bill_customer_sk JOIN store_sales ss ON cs.cs_sold_date_sk = ss.ss_sold_date_sk AND cs.cs_bill_customer_sk = ss.ss_customer_sk WHERE cs.cs_sold_date_sk = 1200;
