SELECT d.d_moy AS month, i.i_category, i.i_class, i.i_brand FROM catalog_sales cs JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk WHERE d.d_year = 2002 GROUP BY d.d_moy, i.i_category, i.i_class, i.i_brand HAVING SUM(cs.cs_ext_sales_price) > 0.001 * ( SELECT SUM(cs2.cs_ext_sales_price) FROM catalog_sales cs2 JOIN date_dim d2 ON cs2.cs_sold_date_sk = d2.d_date_sk WHERE d2.d_year = 2002 ) ORDER BY month, i.i_category, i.i_class, i.i_brand;
