SELECT COALESCE(SUM(CASE WHEN t_hour = 8 THEN ws_quantity END), 0) / NULLIF(SUM(CASE WHEN t_hour = 19 THEN ws_quantity END), 0) AS morning_to_evening_ratio FROM web_sales JOIN time_dim ON ws_sold_time_sk = t_time_sk JOIN customer_demographics ON ws_bill_cdemo_sk = cd_demo_sk JOIN web_page ON ws_web_page_sk = wp_web_page_sk WHERE cd_dep_count = 6 AND wp_char_count > 1000;
