SELECT SUM(ws_ext_discount_amt) AS total_discount FROM web_sales JOIN item ON ws_item_sk = i_item_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE i_manufact_id = 718 AND d_date BETWEEN '2002-03-29' AND '2002-06-26' AND ws_ext_discount_amt > ( SELECT AVG(ws_ext_discount_amt) * 1.3 FROM web_sales JOIN item ON ws_item_sk = i_item_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE i_manufact_id = 718 AND d_date BETWEEN '2002-03-29' AND '2002-06-26' );
