SELECT SUM(ws.ws_ext_discount_amt) AS total_discount FROM web_sales ws JOIN item i ON ws.ws_item_sk = i.i_item_sk JOIN date_dim d ON ws.ws_sold_date_sk = d.d_date_sk WHERE i.i_manufact_id = 350 AND d.d_date BETWEEN '2000-01-27' AND '2000-04-26' AND ws.ws_ext_discount_amt > ( SELECT AVG(ws_inner.ws_ext_discount_amt) * 1.3 FROM web_sales ws_inner JOIN item i_inner ON ws_inner.ws_item_sk = i_inner.i_item_sk JOIN date_dim d_inner ON ws_inner.ws_sold_date_sk = d_inner.d_date_sk WHERE i_inner.i_manufact_id = 350 AND d_inner.d_date BETWEEN '2000-01-27' AND '2000-04-26' ) ;
