SELECT COUNT(*) AS web_sales_count, SUM(ws_ext_ship_cost) AS total_shipping_cost, SUM(ws_net_profit) AS total_net_profit FROM web_sales JOIN date_dim ON ws_sold_date_sk = d_date_sk JOIN customer_address ON ws_ship_addr_sk = ca_address_sk JOIN web_site ON ws_web_site_sk = web_site_sk WHERE d_date BETWEEN '1999-02-01' AND '1999-04-01' AND ca_state = 'IL' AND web_name = 'YourWebSiteName' AND ws_order_number NOT IN (SELECT wr_order_number FROM web_returns) AND ws_warehouse_sk IN ( SELECT ws_warehouse_sk FROM web_sales GROUP BY ws_order_number, ws_warehouse_sk HAVING COUNT(DISTINCT ws_warehouse_sk) > 1 );
