SELECT COUNT(DISTINCT ws_order_number) AS web_sales_count, SUM(wr_return_ship_cost) AS total_shipping_cost, SUM(ws_net_profit) AS net_profit FROM web_sales ws JOIN web_returns wr ON ws.ws_order_number = wr.wr_order_number JOIN customer_address ca ON ws.ws_bill_addr_sk = ca.ca_address_sk JOIN web_site wsite ON ws.ws_web_site_sk = wsite.web_site_sk JOIN date_dim dd ON ws.ws_sold_date_sk = dd.d_date_sk WHERE ca.ca_state = 'given_state' AND wsite.web_name = 'named_web_site' AND dd.d_date BETWEEN DATE '2023-01-01' AND DATE '2023-03-01' AND ws.ws_warehouse_sk <> wr.wr_warehouse_sk GROUP BY ca.ca_state, wsite.web_name;
