SELECT COUNT(DISTINCT ws_order_number) AS web_sales_count, SUM(wr_return_ship_cost) AS total_shipping_cost, SUM(ws_net_profit) AS net_profit FROM web_sales JOIN web_returns ON ws_order_number = wr_order_number JOIN customer_address ON ws_bill_addr_sk = ca_address_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk JOIN web_site ON ws_web_site_sk = web_site_sk WHERE ca_state = 'IN' AND d_month_seq BETWEEN (4 + (2000 - 1900) * 12) AND (4 + (2000 - 1900) * 12 + 1) AND web_name = 'YourWebSiteName' AND ws_warehouse_sk <> wr_warehouse_sk;
