SELECT COUNT(*) AS sales_count FROM store_sales JOIN store ON store_sales.ss_store_sk = store.s_store_sk JOIN customer ON store_sales.ss_customer_sk = customer.c_customer_sk JOIN customer_demographics ON customer.c_current_cdemo_sk = customer_demographics.cd_demo_sk JOIN time_dim ON store_sales.ss_sold_time_sk = time_dim.t_time_sk WHERE store.s_store_name = 'Store Name' AND customer_demographics.cd_dep_count = 2 AND time_dim.t_hour = 14 AND time_dim.t_minute BETWEEN 0 AND 29;
