SELECT COUNT(CASE WHEN ws_promo_sk IS NOT NULL THEN 1 END) AS promotional_sales_count, COUNT(*) AS total_sales_count, COUNT(CASE WHEN ws_promo_sk IS NOT NULL THEN 1 END) * 1.0 / COUNT(*) AS promo_to_total_ratio FROM web_sales JOIN item ON ws_item_sk = i_item_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk JOIN customer_address ON ws_bill_addr_sk = ca_address_sk WHERE i_category = 'particular_item_category' AND d_month_seq = (SELECT d_month_seq FROM date_dim WHERE d_date = DATE '2023-01-01') AND ca_gmt_offset = :given_time_zone;
