SELECT COUNT(CASE WHEN ws_promo_sk IS NOT NULL THEN 1 END) AS promotional_sales_count, COUNT(*) AS total_sales_count, COUNT(CASE WHEN ws_promo_sk IS NOT NULL THEN 1 END) * 1.0 / COUNT(*) AS promo_sales_ratio FROM web_sales JOIN date_dim ON ws_sold_date_sk = d_date_sk JOIN item ON ws_item_sk = i_item_sk JOIN customer_address ON ws_bill_addr_sk = ca_address_sk WHERE i_category = 'Electronics' -- Assuming 'Electronics' as the item category AND d_year = 1999 AND d_moy = 1 -- Assuming January as the month AND ca_gmt_offset = -5.00; -- Assuming GMT-5 as the time zone
