SELECT i.i_item_id, i.i_item_desc, SUM(cs.cs_quantity) AS total_quantity_sold FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_category = 'specified_category' AND d.d_date BETWEEN DATE '2023-01-01' AND DATE '2023-01-30' GROUP BY i.i_item_id, i.i_item_desc;
