SELECT i.i_item_id, i.i_item_desc, i.i_category, SUM(cs.cs_quantity) AS total_quantity_sold FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE d.d_date BETWEEN '2000-05-18' AND '2000-06-16' AND i.i_category = 'Electronics' GROUP BY i.i_item_id, i.i_item_desc, i.i_category ORDER BY total_quantity_sold DESC;
