SELECT CASE WHEN ss_quantity <= 74129 THEN 'Bucket 1' WHEN ss_quantity <= 122840 THEN 'Bucket 2' WHEN ss_quantity <= 56580 THEN 'Bucket 3' WHEN ss_quantity <= 10097 THEN 'Bucket 4' ELSE 'Bucket 5' END AS quantity_bucket, AVG(ss_ext_discount_amt) AS avg_discount_amt, AVG(ss_sales_price) AS avg_sales_price, AVG(ss_list_price) AS avg_list_price, AVG(ss_ext_tax) AS avg_tax, AVG(ss_net_paid) AS avg_net_paid, AVG(ss_net_paid_inc_tax) AS avg_paid_price_inc_tax, AVG(ss_net_profit) AS avg_net_profit FROM store_sales GROUP BY quantity_bucket ORDER BY quantity_bucket;
